import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Chip,
  TextField,
  Button,
  Paper,
  Grid,
  Card,
  CardContent,
  Autocomplete,
  FormControlLabel,
  Checkbox,
  Alert,
  LinearProgress,
  Tab,
  Tabs,
  Divider,
} from '@mui/material';
import {
  Create as CreateIcon,
  Download as DownloadIcon,
  Email as EmailIcon,
  Topic as TopicIcon,
  Link as LinkIcon,
  Refresh as RefreshIcon,
  Preview as PreviewIcon,
} from '@mui/icons-material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import axios from 'axios';

// Types
interface TrendingTopic {
  title: string;
  description: string;
  keywords: string[];
  category: string;
  relevance: number;
}

interface TechnologyRiversLink {
  text: string;
  url: string;
  category: string;
}

interface BlogPost {
  title: string;
  summary: string;
  content: string;
  topic: string;
  keywords: string[];
  wordCount: number;
  readingTime: number;
  status: string;
  createdAt: string;
}

interface BlogGenerationRequest {
  topic: string;
  keywords: string[];
  targetWordCount: number;
  tone: string;
  includeRegulatoryInfo: boolean;
  selectedLinks: string[];
}

// Technology Rivers Links
const TECHNOLOGY_RIVERS_LINKS: TechnologyRiversLink[] = [
  {
    text: 'HIPAA Compliant Mobile App Development Checklist',
    url: 'https://technologyrivers.com/hipaa-compliant-mobile-app-development-checklist/',
    category: 'compliance'
  },
  {
    text: 'Top 8 AI Tools You Need to Know About',
    url: 'https://technologyrivers.com/top-8-ai-tools-you-need-to-know/',
    category: 'ai'
  },
  {
    text: '13 Proven Strategies to Boost Your Mobile App',
    url: 'https://technologyrivers.com/free-app-promotion-playbook/',
    category: 'marketing'
  },
  {
    text: 'AI-Driven Development Services',
    url: 'https://technologyrivers.com/ai-driven-development/',
    category: 'development'
  },
  {
    text: 'HIPAA Compliant AI Healthcare Apps',
    url: 'https://technologyrivers.com/blog/hipaa-compliant-ai-healthcare-apps/',
    category: 'healthcare'
  },
  {
    text: 'Contact Us for Healthcare Software Development',
    url: 'https://technologyrivers.com/contact-us/',
    category: 'contact'
  },
  {
    text: 'The Ultimate Checklist for Software Development',
    url: 'https://technologyrivers.com/the-ultimate-checklist-for-software-development/',
    category: 'development'
  },
  {
    text: '7 Steps to Developing a HIPAA-Compliant Healthcare App',
    url: 'https://technologyrivers.com/blog/7-steps-to-developing-a-hipaa-compliant-healthcare-app/',
    category: 'healthcare'
  },
  {
    text: 'Top HIPAA Compliant Cloud Hosting for Startup and Enterprise',
    url: 'https://technologyrivers.com/blog/top-hipaa-compliant-cloud-hosting-for-startup-and-enterprise/',
    category: 'cloud'
  }
];

// Theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#2c3e50',
    },
    secondary: {
      main: '#3498db',
    },
    background: {
      default: '#f8f9fa',
    },
  },
  typography: {
    h4: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 500,
    },
  },
});

const API_BASE = 'http://localhost:3000';

function App() {
  // State
  const [selectedTopic, setSelectedTopic] = useState<string>('');
  const [customTopic, setCustomTopic] = useState<string>('');
  const [keywords, setKeywords] = useState<string[]>([]);
  const [keywordInput, setKeywordInput] = useState<string>('');
  const [selectedLinks, setSelectedLinks] = useState<string[]>([]);
  const [targetWordCount, setTargetWordCount] = useState<number>(1200);
  const [tone, setTone] = useState<string>('professional');
  const [includeRegulatoryInfo, setIncludeRegulatoryInfo] = useState<boolean>(true);
  const [trendingTopics, setTrendingTopics] = useState<TrendingTopic[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [generatedBlog, setGeneratedBlog] = useState<BlogPost | null>(null);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [currentTab, setCurrentTab] = useState<number>(0);

  // Load trending topics on component mount
  useEffect(() => {
    loadTrendingTopics();
  }, []);

  const loadTrendingTopics = async () => {
    try {
      const response = await axios.get(`${API_BASE}/topic-research/trending`);
      setTrendingTopics(response.data);
    } catch (error) {
      console.error('Failed to load trending topics:', error);
      setError('Failed to load trending topics');
    }
  };

  const handleTopicSelect = (topic: TrendingTopic) => {
    setSelectedTopic(topic.title);
    setCustomTopic('');
    setKeywords(topic.keywords);

    // Auto-select relevant links based on topic keywords
    const relevantLinks = TECHNOLOGY_RIVERS_LINKS
      .filter(link => 
        topic.keywords.some(keyword => 
          link.text.toLowerCase().includes(keyword.toLowerCase()) ||
          link.category.toLowerCase().includes(keyword.toLowerCase())
        )
      )
      .map(link => link.url)
      .slice(0, 3);

    setSelectedLinks(relevantLinks);
  };

  const handleCustomTopicChange = (value: string) => {
    setCustomTopic(value);
    setSelectedTopic('');

    // Auto-suggest keywords based on custom topic
    const suggestedKeywords = [];
    if (value.toLowerCase().includes('ai')) suggestedKeywords.push('AI');
    if (value.toLowerCase().includes('hipaa')) suggestedKeywords.push('HIPAA compliance');
    if (value.toLowerCase().includes('mobile')) suggestedKeywords.push('mobile app development');
    if (value.toLowerCase().includes('cloud')) suggestedKeywords.push('cloud hosting');
    if (value.toLowerCase().includes('security')) suggestedKeywords.push('security');

    setKeywords(suggestedKeywords);
  };

  const addKeyword = () => {
    if (keywordInput.trim() && !keywords.includes(keywordInput.trim())) {
      setKeywords([...keywords, keywordInput.trim()]);
      setKeywordInput('');
    }
  };

  const removeKeyword = (keywordToRemove: string) => {
    setKeywords(keywords.filter(k => k !== keywordToRemove));
  };

  const toggleLink = (linkUrl: string) => {
    if (selectedLinks.includes(linkUrl)) {
      setSelectedLinks(selectedLinks.filter(url => url !== linkUrl));
    } else {
      setSelectedLinks([...selectedLinks, linkUrl]);
    }
  };

  const generateBlog = async () => {
    if (!selectedTopic && !customTopic) {
      setError('Please select or enter a topic');
      return;
    }

    if (keywords.length === 0) {
      setError('Please add at least one keyword');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const request: BlogGenerationRequest = {
        topic: selectedTopic || customTopic,
        keywords,
        targetWordCount,
        tone,
        includeRegulatoryInfo,
        selectedLinks,
      };

      const response = await axios.post(`${API_BASE}/blog-generator/generate`, request);
      setGeneratedBlog(response.data.blogPost);
      setSuccess('Blog generated successfully!');
      setCurrentTab(1); // Switch to preview tab
    } catch (error: any) {
      setError('Failed to generate blog: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const downloadBlog = async (format: 'docx' | 'html') => {
    if (!generatedBlog) return;

    try {
      const response = await axios.post(
        `${API_BASE}/blog-generator/generate-docx`,
        generatedBlog,
        { responseType: 'blob' }
      );

      const blob = new Blob([response.data], {
        type: format === 'docx' 
          ? 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
          : 'text/html'
      });

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${generatedBlog.title.toLowerCase().replace(/\s+/g, '_')}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      setError('Failed to download blog');
    }
  };

  const emailBlog = async () => {
    if (!generatedBlog) return;

    try {
      setLoading(true);
      await axios.post(`${API_BASE}/blog-generator/generate-and-email`, generatedBlog);
      setSuccess('Blog emailed to marketing team successfully!');
    } catch (error: any) {
      setError('Failed to email blog: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center" color="primary">
          AutoBlog AI - Blog Generation Dashboard
        </Typography>
        <Typography variant="subtitle1" align="center" color="text.secondary" sx={{ mb: 4 }}>
          Generate professional software development blogs with AI and Technology Rivers expertise
        </Typography>

        <Paper sx={{ mb: 3 }}>
          <Tabs
            value={currentTab}
            onChange={(_, newValue) => setCurrentTab(newValue)}
            variant="fullWidth"
            sx={{ borderBottom: 1, borderColor: 'divider' }}
          >
            <Tab label="Generate Blog" icon={<CreateIcon />} />
            <Tab label="Preview & Actions" icon={<PreviewIcon />} disabled={!generatedBlog} />
          </Tabs>
        </Paper>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}

        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}

        {loading && <LinearProgress sx={{ mb: 2 }} />}

        {/* Tab 1: Generation Form */}
        {currentTab === 0 && (
          <Box>
            <Grid container spacing={3}>
              {/* Left Column - Topic Selection */}
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    <TopicIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
                    Topic Selection
                  </Typography>

                  <Box sx={{ mb: 3 }}>
                    <Typography variant="subtitle2" gutterBottom>
                      Suggested Software Development Topics
                    </Typography>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={loadTrendingTopics}
                      startIcon={<RefreshIcon />}
                      sx={{ mb: 2 }}
                    >
                      Refresh Topics
                    </Button>
                    <Box sx={{ maxHeight: 300, overflowY: 'auto' }}>
                      {trendingTopics.map((topic, index) => (
                        <Card 
                          key={index} 
                          sx={{ 
                            mb: 1, 
                            cursor: 'pointer',
                            border: selectedTopic === topic.title ? 2 : 1,
                            borderColor: selectedTopic === topic.title ? 'primary.main' : 'grey.300'
                          }}
                          onClick={() => handleTopicSelect(topic)}
                        >
                          <CardContent sx={{ py: 1 }}>
                            <Typography variant="subtitle2" color="primary">
                              {topic.title}
                            </Typography>
                            <Typography variant="body2" color="text.secondary" sx={{ fontSize: '0.8rem' }}>
                              {topic.description}
                            </Typography>
                            <Box sx={{ mt: 1 }}>
                              {topic.keywords.map((keyword, idx) => (
                                <Chip 
                                  key={idx} 
                                  label={keyword} 
                                  size="small" 
                                  sx={{ mr: 0.5, mb: 0.5, fontSize: '0.7rem' }} 
                                />
                              ))}
                            </Box>
                          </CardContent>
                        </Card>
                      ))}
                    </Box>
                  </Box>

                  <Divider sx={{ my: 2 }} />

                  <Box>
                    <Typography variant="subtitle2" gutterBottom>
                      Or Enter Custom Topic
                    </Typography>
                    <TextField
                      fullWidth
                      placeholder="e.g., Solving API Integration Challenges with Modern Tools"
                      value={customTopic}
                      onChange={(e) => handleCustomTopicChange(e.target.value)}
                      sx={{ mb: 2 }}
                    />
                  </Box>
                </Paper>
              </Grid>

              {/* Right Column - Configuration */}
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Blog Configuration
                  </Typography>

                  {/* Keywords */}
                  <Box sx={{ mb: 3 }}>
                    <Typography variant="subtitle2" gutterBottom>
                      Keywords
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 1, mb: 1 }}>
                      <TextField
                        size="small"
                        placeholder="Add keyword..."
                        value={keywordInput}
                        onChange={(e) => setKeywordInput(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && addKeyword()}
                        sx={{ flexGrow: 1 }}
                      />
                      <Button variant="outlined" onClick={addKeyword}>
                        Add
                      </Button>
                    </Box>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                      {keywords.map((keyword, index) => (
                        <Chip
                          key={index}
                          label={keyword}
                          onDelete={() => removeKeyword(keyword)}
                          color="primary"
                          variant="outlined"
                          size="small"
                        />
                      ))}
                    </Box>
                  </Box>

                  {/* Settings */}
                  <Grid container spacing={2} sx={{ mb: 3 }}>
                    <Grid item xs={6}>
                      <TextField
                        fullWidth
                        label="Word Count"
                        type="number"
                        value={targetWordCount}
                        onChange={(e) => setTargetWordCount(Number(e.target.value))}
                        inputProps={{ min: 500, max: 3000 }}
                      />
                    </Grid>
                    <Grid item xs={6}>
                      <Autocomplete
                        options={['professional', 'casual', 'technical', 'executive']}
                        value={tone}
                        onChange={(_, newValue) => setTone(newValue || 'professional')}
                        renderInput={(params) => <TextField {...params} label="Tone" />}
                      />
                    </Grid>
                  </Grid>

                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={includeRegulatoryInfo}
                        onChange={(e) => setIncludeRegulatoryInfo(e.target.checked)}
                      />
                    }
                    label="Include Regulatory Information"
                    sx={{ mb: 2 }}
                  />
                </Paper>

                {/* Technology Rivers Links */}
                <Paper sx={{ p: 3, mt: 2 }}>
                  <Typography variant="h6" gutterBottom>
                    <LinkIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
                    Technology Rivers Links
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Select links to incorporate in your blog
                  </Typography>
                  <Box sx={{ maxHeight: 300, overflowY: 'auto' }}>
                    {TECHNOLOGY_RIVERS_LINKS.map((link, index) => (
                      <Box key={index} sx={{ mb: 1 }}>
                        <FormControlLabel
                          control={
                            <Checkbox
                              checked={selectedLinks.includes(link.url)}
                              onChange={() => toggleLink(link.url)}
                            />
                          }
                          label={
                            <Box>
                              <Typography variant="body2" sx={{ fontWeight: 500 }}>
                                {link.text}
                              </Typography>
                              <Typography variant="caption" color="text.secondary">
                                {link.category}  {link.url}
                              </Typography>
                            </Box>
                          }
                        />
                      </Box>
                    ))}
                  </Box>
                </Paper>
              </Grid>

              {/* Generate Button */}
              <Grid item xs={12}>
                <Box sx={{ textAlign: 'center', mt: 2 }}>
                  <Button
                    variant="contained"
                    size="large"
                    onClick={generateBlog}
                    disabled={loading || (!selectedTopic && !customTopic) || keywords.length === 0}
                    startIcon={<CreateIcon />}
                    sx={{ px: 4, py: 1.5 }}
                  >
                    {loading ? 'Generating Blog...' : 'Generate Blog'}
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </Box>
        )}

        {/* Tab 2: Preview & Actions */}
        {currentTab === 1 && generatedBlog && (
          <Box>
            <Grid container spacing={3}>
              <Grid item xs={12} md={8}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Blog Preview
                  </Typography>
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="h5" color="primary" gutterBottom>
                      {generatedBlog.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                      {generatedBlog.summary}
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
                      <Chip label={`${generatedBlog.wordCount} words`} size="small" />
                      <Chip label={`${generatedBlog.readingTime} min read`} size="small" />
                      <Chip label={generatedBlog.topic} size="small" color="primary" />
                    </Box>
                    <Box sx={{ mb: 2 }}>
                      {generatedBlog.keywords.map((keyword, index) => (
                        <Chip key={index} label={keyword} size="small" sx={{ mr: 0.5, mb: 0.5 }} />
                      ))}
                    </Box>
                  </Box>
                  <Divider sx={{ my: 2 }} />
                  <Box 
                    sx={{ 
                      maxHeight: 400, 
                      overflowY: 'auto',
                      '& h1, & h2, & h3': { color: 'primary.main' },
                      '& ul': { pl: 3 },
                      '& li': { mb: 1 }
                    }}
                    dangerouslySetInnerHTML={{ __html: generatedBlog.content }}
                  />
                </Paper>
              </Grid>

              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Actions
                  </Typography>

                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                    <Button
                      variant="contained"
                      fullWidth
                      onClick={() => downloadBlog('docx')}
                      startIcon={<DownloadIcon />}
                    >
                      Download DOCX
                    </Button>

                    <Button
                      variant="outlined"
                      fullWidth
                      onClick={() => downloadBlog('html')}
                      startIcon={<DownloadIcon />}
                    >
                      Download HTML
                    </Button>

                    <Button
                      variant="contained"
                      color="secondary"
                      fullWidth
                      onClick={emailBlog}
                      disabled={loading}
                      startIcon={<EmailIcon />}
                    >
                      Email to Marketing Team
                    </Button>

                    <Button
                      variant="outlined"
                      fullWidth
                      onClick={() => setCurrentTab(0)}
                    >
                      Generate Another Blog
                    </Button>
                  </Box>
                </Paper>

                {/* Blog Statistics */}
                <Paper sx={{ p: 3, mt: 2 }}>
                  <Typography variant="h6" gutterBottom>
                    Blog Statistics
                  </Typography>
                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                    <Typography variant="body2">
                      <strong>Topic:</strong> {generatedBlog.topic}
                    </Typography>
                    <Typography variant="body2">
                      <strong>Word Count:</strong> {generatedBlog.wordCount}
                    </Typography>
                    <Typography variant="body2">
                      <strong>Reading Time:</strong> {generatedBlog.readingTime} minutes
                    </Typography>
                    <Typography variant="body2">
                      <strong>Generated At:</strong> {new Date(generatedBlog.createdAt).toLocaleString()}
                    </Typography>
                    <Typography variant="body2">
                      <strong>Status:</strong> {generatedBlog.status}
                    </Typography>
                  </Box>
                </Paper>
              </Grid>
            </Grid>
          </Box>
        )}
      </Container>
    </ThemeProvider>
  );
}

export default App;
